package com.gg.server.config;import org.springframework.beans.factory.annotation.Value;import org.springframework.stereotype.Component;@Componentpublic class FileServiceConfig {    @Value("${file-service.classId-vod}")    private String vodId;    @Value("${file-service.classId-img}")    private String imgId;    @Value("${file-service.partnerId}")    private String partnerId;    @Value("${file-service.partnerKey}")    private String partnerKey;    public String getVodId() {        return vodId;    }    public void setVodId(String vodId) {        this.vodId = vodId;    }    public String getImgId() {        return imgId;    }    public void setImgId(String imgId) {        this.imgId = imgId;    }    public String getPartnerId() {        return partnerId;    }    public void setPartnerId(String partnerId) {        this.partnerId = partnerId;    }    public String getPartnerKey() {        return partnerKey;    }    public void setPartnerKey(String partnerKey) {        this.partnerKey = partnerKey;    }}